import { Container, Paper } from "@material-ui/core/";
import MaterialTable from 'material-table'

export const SearchResults=() => {
  // const searchResults = "Search Results go here."; 

  //const[data, setData] = useState([])
  const columns = [
    {title:'Date', field:'CON_DATE'},
    {title:'Contributor', field:'CON_NAME'},
    {title:'Candidate', field:'cand_name'},
    {title:'Committee', field:'CMT_NAME', render:(data)=>{
      return `${data.CMT_NAME}  [${data.CON_ID}]`
    }},
    {title:'Amount', field:'CON_AMOUNT'}
  ]

  /*useEffect(() => {
    fetch("https://ethics.lacity.org/opendata/opendata_cmp_stmt_contributions_public_test.json")
      .then(resp => resp.json())
      .then(resp => {
        setData(resp)
        console.log(resp)
      })
  }, [])*/



