  {
    title:'firstName', field:'first_name', render:(data)=>{
     return `${data.name} ${data.last_name}`;
   }
  },
  {
    title:'lastName', field:'last_name'
  }
]
*/
  const data = require('./data.json');
  console.log(data);

  return (
    <Container>
      <Paper>
        <MaterialTable title="Search Candidate Contributions"
          columns={columns}
          data={data}
          localization={{
            toolbar: {
              searchPlaceholder: 'Enter Contributor Name'
            }
          }}
          options={{
            exportButton: true
          }}      
        />
      </Paper>
    </Container>
  );
}

export default SearchResults;
